color: rgb(0, 0, 0) !important;
}

.featured-product {   margin: 8% !important;
}

.product-page-button {
    background: rgb(0, 0, 0) !important; 
    color: white !important; font-weight: 700 !important; font-size: 2rem !important;  
}

settings.customUGCBlockCss:
* {font-family: Assistant !important;  }

#ambassador-ugc-block {
  margin: 0 80px 0 80px;
}

@media only screen and (max-width: 768px) {
  #ambassador-ugc-block {
    margin:  20px;
  }

settings.customProductCss:
* {font-family: Assistant !important; 
}


.card-wrapper {
  height: auto !important;
}

.card {
  border: 1px solid rgba(24,24,24);
  background-color: rgba(241,241,241);
}

.card__heading {
  font-weight: normal !important;
  font-size: 16px !important;
}

.price {
  font-size: 13px !important;
}

.card__information {
  padding: 0 10px 10px;
}

settings.customContainerCss:
.collection {
  margin: 50px 80px 35px 80px !important;
}

.collection__title {
  margin-bottom: 20px !important;
}

@media only screen and (max-width: 768px) {
  .collection {
    margin:  20px !important;
  }

settings.customProductPickerContainerCss:
.lc-product-modal-edit-button{
padding-bottom: 12% !important;
}

.heart-icon-wrapper {
    position: absolute;
    padding: 10px 12px 0px;
    border-radius: 10px;
    color: black;
    background: white;
    cursor: pointer;

settings.customHeroBlockCss:
* {font-family: Assistant !important; }

.ambassador_hero__card { 
  background-color: #fefdfd;
  color: #181818;
}

.ambassador-hero__quote-slider___controls {
  margin-top: 5px;
}

.ambassador-hero__followercount {
  margin: 5px 0px 15px !important;
}

.ambassador-hero__brandname {
  visibility: hidden;
}

.ambassador-hero__quote-slider___controls {
  position: absolute;
  bottom: 15px;
  left: 50%;
  transform: translate(-50%, -50%);
}

.slide-aboutme-content {
  overflow-y: hidden !important;
}

.slide-aboutme .ambassador-hero__aboutlink {
  position: absolute;
  bottom: 50px;
  left: 50%;
  transform: translate(-50%, -50%);
}

.ambassador-hero__heroimage a {
  font-size: 22px !important;
}

.ambassador-hero {
  margin: 50px 80px 35px 80px !important;
}

.slide-aboutme h3 {
  margin-bottom: 5px !important;
}

.slide-aboutme p {
  font-size: 18px !important;
}

.ambassador-hero__aboutlink {
  color: #181818 !important;
  border: 1px solid #181818 !important;
}

.ambassador-hero__quote-slider___controls .active {
  background-color: #181818
  border: 1px solid #181818 !important;
}

.ambassador-hero__quote-slider___controls span {
  border: 1px solid #181818 !important;
}





@media only screen and (max-width: 768px) {
  .ambassador-hero {
    margin:  20px !important;
  }
  .ambassador-hero__heroimage a {
    font-size: 18px !important;
  }
  .ambassador-hero__username {
    font-size: 20px !important;
  }
  .slide-intro {
    padding: 50px 75px 0px;
  }
  .slide-aboutme-content {
    margin: 20px !important;
  }
  .ambassador-hero__aboutme-title {
    font-size: 18px !important;
  }
  .slide-aboutme p {
    font-size: 16px !important;
  }
}
